    return (
      <Wrapper>
        <Title>Friends List</Title>
        {friends.map((friend) => (
          <FriendCard
            onClick={() => {
              this.shuffle();
            }}
            id={friend.id}
            key={friend.id}
            name={friend.name}
            image={friend.image}
            occupation={friend.occupation}
            location={friend.location}
          />
        ))}
      </Wrapper>
    );
